import React, { useState, useEffect, Component} from 'react';

class Game extends Component{
  state = {correctAnswers: 0, questionIndex: 0, question : this.props.questions[this.state.questionIndex], isEndScreen:false, len: this.props.questions.length, time:{}, seconds:5}
  timer = 0;

  startTimer = () => {
    if (this.timer === 0 && this.state.seconds > 0) {
      this.timer = setInterval(this.countDown, 1000);
    }
  }

  checkAnswer = (response, answer) => {
    console.log(response, answer, length)
    if (response === answer) {              
      setCorrectAnswers(correctAnswers + 1);
    }
    var newIndex = this.state.questionIndex + 1;
    setQuestionIndex(newIndex);
    setQuestion(props.questions[this.state.questionIndex+1])

    if (this.state.questionIndex+1 === length) {
      setIsEndScreen(true);
    }
  }
  render() {
  return (
  <div>
    {console.log(timer)}
    {isEndScreen ? 
    <div>
      You got {correctAnswers}/{length} correct.
      <button onClick={props.callBackFunction}>Return to home</button>
  </div> : 
  <div>
      <div key={question.id}>
      <p>{question.name}</p>
      {question.responses.map(response => <button onClick={() => {console.log(question);checkAnswer(response, question.answer, props.questions.length)}}>{response}</button>)}
      </div>
    <br/>
    Correct Answers: {correctAnswers}
    Time: {timer}
    Fix the timer!
    </div>}
  </div>) }
}

export default Game;
