import React, {useState, useEffect} from 'react';
import { useTimer } from 'react-timer-hook';
import axios from 'axios';

function Game(props) {
  let questions = [];
  const [requested, setRequested] = useState(false);
  const [correctAnswers, setCorrectAnswers] = useState(0);
  const [questionIndex, setQuestionIndex] = useState(0);
  const [question, setQuestion] = useState();
  const [isEndScreen, setIsEndScreen] = useState(false);
  const [len, setLen] = useState(questions.length);
  const totalTime = len*10//questions.length;
  const initTime = new Date();
  initTime.setSeconds(initTime.getSeconds() + totalTime);
  const [expiryTimestamp, setExpiryTimestamp] = useState(initTime);
  const {
    seconds,
    minutes,
    hours,
    days,
    isRunning,
    start,
    pause,
    resume,
    restart
  } = useTimer({
    expiryTimestamp,
    onExpire: () => console.warn("onExpire called")
  });

  const checkAnswer = (response, answer) => {
    console.log(response, answer, len)
    if (response === answer) {   
      setCorrectAnswers(correctAnswers+1);          
    }
    var newIndex = questionIndex + 1;
    setQuestionIndex(newIndex);
    setQuestion(questions[questionIndex+1])


    if (questionIndex+1 === len) {
      setIsEndScreen(true)
    }
  }

  //if (minutes === 0 && seconds === 0 && !isEndScreen) {
  //  setIsEndScreen(true);
  //}

  return (
  <div>
    {!question ? <div>No questions</div> : 
    <div>
      {isEndScreen ? 
    <div>
      You got {correctAnswers}/{len} correct.
  </div> : 
  <div>
      <div key={question.id}>
      <p>{question.name}</p>
      {question.responses.map(response => <button onClick={() => {console.log(question);checkAnswer(response, question.answer, questions.length)}}>{response}</button>)}
      </div>
    <br/>
    Correct Answers: {correctAnswers}
    <div>
    {(minutes.toString()).padStart(2, "0")}:{(seconds.toString()).padStart(2, "0")}
    </div>
    </div>}
    </div>} 
  </div>) 
}

export default Game;
