import React, { useState} from 'react';

function Game(props) {
    const [correctAnswers, setCorrectAnswers] = useState(0);
    //const [question, setQuestion] = useState(Object.keys(props.questions)[questionIndex]);
    //const [questionData, setQuestionData] = useState(Object.values(props.questions)[questionIndex]);

    function checkAnswer(response, answer){
        if (response === answer) {
            setCorrectAnswers(correctAnswers + 1)
        }
    }

    for (const question in props.questions.values()) {
        console.log(question)
    }
    return (
    <div>
        {props.questions.map((question) => <div key={question.id}>
            <p>{question.name}</p>
            {question.responses.map(response => {<button onClick={() => checkAnswer(response, question.answser)}>{response}</button>})}
            </div>)}
    </div>)
}

export default Game;
