import React, { Component } from 'react';
import Game from './Game';
import Create from './Create'
import axios from 'axios';

class App extends Component {
  state = {questions: {}, requested: false, isPlaying: false, isCreate: false};
  homeScreen = <button onClick={(event) => this.setState({isPlaying: true})}>Play game</button>

  getQuestions = () => {
    if (!this.state.requested) { 
      axios({
        method: 'get',
        url: 'http://127.0.0.1:8000/api/mvp/'
      })
      .then(response => {
        this.setState({questions: response.data, requested: true});
      }) }}

  createQuestionJSX = () => {
    return(
      <div>
        {this.state.isCreate ? <Create/> : <button onClick={(event) => this.setState({isCreate: true})}>Create question</button>}
      </div>
    )
  }

  render() {
    this.getQuestions();
    return(
    <div>
      {this.createQuestionJSX()}
      {this.state.isPlaying && !this.state.isCreate ? 
      <Game questions={this.state.questions} callBackFunction={() => this.setState({isPlaying: false, requested: false})}/>: 
      this.homeScreen}
    </div>);
  }
}

export default App;
