import React, { useState} from 'react';

function Game(props) {
    const [correctAnswers, setCorrectAnswers] = useState(0);
    const [questionIndex, setQuestionIndex] = useState(0);
    const [question, setQuestion] = useState(props.questions[questionIndex]);

    //const [question, setQuestion] = useState(Object.keys(props.questions)[questionIndex]);
    //const [questionData, setQuestionData] = useState(Object.values(props.questions)[questionIndex]);

    function checkAnswer(response, answer, length){
        console.log(response, answer, length)
        if (response === answer) {            setCorrectAnswers(correctAnswers + 1);
            var newIndex = questionIndex + 1;
            setQuestionIndex(newIndex);
            setQuestion(props.questions[questionIndex]);
        }
    }

    return (
    <div>
        <div key={question.id}>
            <p>{question.name}</p>
            {question.responses.map(response => <button onClick={() => {console.log(questionIndex);checkAnswer(response, question.answer, props.questions.length)}}>{response}</button>)}
            </div>
        <br/>
        Correct Answers: {correctAnswers}
        Question ID: {questionId}
    </div>)
}

export default Game;
