import React, {useState, useEffect} from 'react';
import { useTimer } from 'react-timer-hook';

function Game(props) {
  const [correctAnswers, setCorrectAnswers] = useState(0);
  const [questionIndex, setQuestionIndex] = useState(0);
  const [question, setQuestion] = useState(props.questions[0]);
  const [isEndScreen, setIsEndScreen] = useState(false);
  const [len, setLen] = useState(props.questions.length);

  checkAnswer = (response, answer) => {
    console.log(response, answer, this.state.len)
    if (response === answer) {   
      this.setState({correctAnswers : this.state.correctAnswers + 1})           
    }
    var newIndex = this.state.questionIndex + 1;
    this.setState({questionIndex: newIndex, question: props.questions[this.state.questionIndex+1]})

    if (this.state.questionIndex+1 === this.state.len) {
      this.setState({isEndScreen: true})
    }
  }

  return (
  <div>
    {console.log(this.timer)}
    {this.state.isEndScreen ? 
    <div>
      You got {this.state.correctAnswers}/{this.state.len} correct.
      <button onClick={props.callBackFunction}>Return to home</button>
  </div> : 
  <div>
      <div key={this.state.question.id}>
      <p>{this.state.question.name}</p>
      {this.state.question.responses.map(response => <button onClick={() => {console.log(this.state.question);this.checkAnswer(response, this.state.question.answer, props.questions.length)}}>{response}</button>)}
      </div>
    <br/>
    Correct Answers: {this.state.correctAnswers}
    Time: {this.timer}
    Fix the timer!
    </div>}
  </div>) 
}

export default Game;
