import React, { Component } from 'react';
import Game from './Game';
import Create from './Create';
import axios from 'axios';

import { BrowserRouter, Routes, Route, Link } from "react-router-dom";

class Renders extends Component {
  state = { routesList: ["testRoute", "testRoute2"] };

  addRoute = (route, category) => {
    this.setState(
      (previousState) => ({
        routesList: [...previousState.routesList, route]
      }),
      console.log(this.state.routesList)
    );
  };

  render() {
    return (
      <BrowserRouter>
        <ul>
          <li>
            <Link to="/">Home</Link>
          </li>
          {this.state.routesList.map((path) => (
            <li key={path}>
              <Link to={`/${path}`}>{path}</Link>
            </li>
          ))}
        </ul>
        <Routes>
          <Route path="/" element={<App callBackFunction={this.addRoute} />} />
          {this.state.routesList.map((routePoint) => (
            <Route
              key={routePoint}
              path={["/", routePoint].join("")}
              element={<p>This route is /{routePoint}</p>}
            />
          ))}
        </Routes>
      </BrowserRouter>
    );
  }
}

const makeid = (length) => {
  var result           = '';
  var characters       = 'ABCDEFGHIJKLMNOPQRSTUVWXYZabcdefghijklmnopqrstuvwxyz0123456789';
  var charactersLength = characters.length;
  for ( var i = 0; i < length; i++ ) {
    result += characters.charAt(Math.floor(Math.random() * 
charactersLength));
 }
 return result;
}

class App extends Component {
  state = {questions: {}, requested: false, isPlaying: false, isCreate: false};

  getQuestions = () => {
    if (!this.state.requested) { 
      axios({
        method: 'get',
        url: 'http://127.0.0.1:8000/api/'
      })
      .then(response => {
        this.setState({questions: response.data, requested: true});console.log(this.state.questions);
      }) }};

  render() {
    this.getQuestions();
    return(
    <div>
      {(this.state.isPlaying && !this.state.isCreate) ? 
      <Game questions={this.state.questions} callBackFunction={() => this.setState({isPlaying: false, requested: false})}/>: 
      <div>{(this.state.isCreate && !this.state.isPlaying) ? <Create callBackFunction={() => this.setState({isCreate: false})}/> : 
        <div><button onClick={(event) => this.setState({isPlaying: true})}>Play game</button><button onClick={(event) => this.setState({isCreate: true})}>Create question</button></div>}</div>}
      <button onClick={() => this.props.callBackFunction(makeid(7), "")}>Create link</button> 
    </div>);
  }
}

export default App;
